private void jButton1ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton1ActionPerformed
   produto = new Produto();
   produto.setCodigo(Integer.parseInt(jTxtId.getText()));
   produto.setNome(jTxtNome.getText());
   produto.setValor(Float.parseFloat(jTxtValor.getText()));
   produto.setPreco();
   JOptionPane.showMessageDialog(null, produto.getNome() + '\n' + produto.getCodigo()
    + '\n' + produto.getValor() + '\n' + produto.getPreco()
   );
    
    
}//GEN-LAST:event_jButton1ActionPerformed


